import { ref } from 'vue'
import { db } from '../firebase/config'
import { collection, onSnapshot, query, where } from 'firebase/firestore'

const getOrderDocs = (c, keyField, fieldValue) => {

  const error = ref(null)
  let documents
  // register the firestore collection reference
  let colRef = collection(db, c)
  if (keyField==='course'){
     colRef = query(colRef, where("course", "==", fieldValue))
  } else {
     colRef = query(colRef, where("module", "==", fieldValue))
  }


  const unsub = onSnapshot(colRef, snap => {
    let results = []
    snap.docs.forEach(doc => {
      // must wait for the server to create the timestamp & send it back
      results.push({...doc.data(), id: doc.id})
    });

    // update values
    documents = results
    // update values
    if (keyField==='course') {
      documents.sort((a, b) => (a.modnumb > b.modnumb) ? 1 : -1)
    } else {
      documents.sort((a, b) => (a.order > b.order) ? 1 : -1)
    }
    return documents
    error.value = null
  }, err => {
    console.log(err.message)
    error.value = 'could not fetch the data'
  })
  console.log("2: ", results)

  return { error, documents }
}

export default getOrderDocs
